css_content:
.educational-content {
            max-width: 800px;
            margin: 0 auto;
            padding: 20px;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333;
            background: #fff;
        }

        .content-header {
            text-align: center;
            margin-bottom: 30px;
            padding: 20px;
            background: #f8f9fa;
            border: 1px solid #e9ecef;
            border-radius: 8px;
        }

        .content-header h1 {
            margin: 0 0 10px 0;
            font-size: 2.2em;
            color: #2c3e50;
        }

        .course-level {
            margin: 0;
            font-size: 1.1em;
            color: #6c757d;
        }

        .content-main section {
            margin-bottom: 25px;
            padding: 20px;
            background: #f8f9fa;
            border: 1px solid #e9ecef;
            border-radius: 6px;
        }

        .content-main h2 {
            color: #2c3e50;
            margin-top: 0;
            margin-bottom: 15px;
            font-size: 1.6em;
            border-bottom: 2px solid #3498db;
            padding-bottom: 5px;
        }

        .content-main h3 {
            color: #34495e;
            margin-top: 20px;
            margin-bottom: 10px;
            font-size: 1.3em;
        }

        .content-main p {
            margin-bottom: 15px;
            text-align: justify;
        }

        .content-main ul {
            margin: 15px 0;
            padding-left: 20px;
        }

        .content-main li {
            margin-bottom: 8px;
        }

        .exercise-item {
            margin-bottom: 20px;
            padding: 15px;
            background: white;
            border: 1px solid #dee2e6;
            border-radius: 4px;
        }

        .exercise-item h3 {
            color: #495057;
            margin-top: 0;
            margin-bottom: 10px;
        }

        .exercise-item textarea {
            width: 100%;
            margin-top: 10px;
            padding: 10px;
            border: 1px solid #ced4da;
            border-radius: 4px;
            font-family: inherit;
            resize: vertical;
            font-size: 14px;
        }

        .quiz-question {
            margin-bottom: 20px;
            padding: 15px;
            background: white;
            border: 1px solid #dee2e6;
            border-radius: 4px;
        }

        .quiz-question h3 {
            color: #495057;
            margin-top: 0;
            margin-bottom: 10px;
        }

        .quiz-question input[type="radio"] {
            margin-right: 8px;
        }

        .quiz-question br {
            margin-bottom: 5px;
        }

        @media (max-width: 768px) {
            .educational-content {
                padding: 15px;
            }

            .content-header h1 {
                font-size: 1.8em;
            }

            .content-main section {
                padding: 15px;
            }
        }

js_content:
document.addEventListener('DOMContentLoaded', function() {
            // Interactividad básica para textareas
            const textareas = document.querySelectorAll('textarea');
            textareas.forEach(textarea => {
                textarea.addEventListener('input', function() {
                    this.style.borderColor = '#3498db';
                });
            });

            // Interactividad básica para radio buttons
            const radioButtons = document.querySelectorAll('input[type="radio"]');
            radioButtons.forEach(radio => {
                radio.addEventListener('change', function() {
                    const question = this.closest('.quiz-question');
                    question.style.backgroundColor = '#e8f4f8';
                });
            });
        });
